#
# This shell script parses text files that contain aggregated statistics generated   
# by NENoCSi with respect to maximum latencies and appends individual results along    
# with the respective acceleration factor to a new text file.                        
#                                                                                    
# Note: This script needs to receive NENoCSi's binary directory as first parameter, the
#       network topology as second parameter, and the casting type as third parameter
#                                                                                    
#***************************************************************************************
cd $1
echo "# Abbreviations used in this file" > "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
echo "# acc:   acceleration factor" >> "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
echo "# delay: maximum delay" >> "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
echo "# ratio: received/ideal flit ratio" >> "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
echo acc$'\t'delay$'\t'ratio >> "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
for ACC in 1 2 5 10 20 50 100 200 500
do
    printf -v IFNAME "data/$2_6x6_256_npn_$3_acc_%03d.txt" $ACC
    echo ${ACC}\
	$'\t'$(cat $IFNAME | sed '/^% Max delay (cycles):/ ! d; s/^% Max delay (cycles): \([0-9.]*\)$/\1/')\
	$'\t'$(cat $IFNAME | sed '/^% Received\/Ideal flits Ratio:/ ! d; s/^% Received\/Ideal flits Ratio: \([0-9.]*\)$/\1/') >> "data/delay_vs_acc_$2_6x6_256_npn_$3.txt"
done
