#                                                                                    
# This shell script creates three different kinds of plots: A boxplot of latencies   
# and a heatmap of routed flits for each acceleration factor as well as a            
# "maximum delay vs. acceleration factor" plot to assess network performance.        
#                                                                                    
# Note: This script needs to receive NENoCSi's binary directory as first parameter, the
#       network topology as second parameter, and the casting type as third parameter
#                                                                                    
#***************************************************************************************
BINDIR=$1
TOPOLOGY=$2
CASTING=$3
#
DATADIR=${BINDIR}/results/data_${TOPOLOGY}_${CASTING}
mkdir ${DATADIR}/Images
for ACC in 1 2 5 10 20 50 100 200 500
do
    gnuplot -c ../scripts/plot/plot_Delays_TOPOLOGY_6x6_256_npn_CASTING.gnu $DATADIR $TOPOLOGY $CASTING $ACC
    gnuplot -c ../scripts/plot/plot_Flits_Routed_TOPOLOGY_6x6_256_npn_CASTING.gnu $DATADIR $TOPOLOGY $CASTING $ACC
done
gnuplot -c ../scripts/plot/plot_delay_vs_acc_TOPOLOGY_6x6_256_npn_CASTING.gnu $DATADIR $TOPOLOGY $CASTING
